package com.kari.kariboard.controller;import com.kari.kariboard.dto.UserDTO;import com.kari.kariboard.service.UserService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Controller;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RequestMethod;import javax.servlet.http.HttpSession;// view 와 통신하는 페이지 이동을 위한 Controller@Controller@RequestMapping("/user")public class UserController {    @Autowired    private UserService userService;    /**     * 로그인     */    @RequestMapping(value= "/login", method = RequestMethod.POST)    public String logIn(String username, String password, HttpSession session) {        UserDTO u = userService.logIn(username, password);        if(u != null) {            session.setAttribute("logIn", u);            return "/board/selectall";        }        return "redirect:/";    }    /**     * 회원가입     */    @RequestMapping(value = "/register", method = RequestMethod.POST)    public String register(UserDTO u) {        userService.register(u);        return "/user/register";    }}